import React from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import {DataGrid} from '@mui/x-data-grid';
import Cookies from 'js-cookie';
import {SERVER_URL} from '../constants.js';

// NOTE:  for OAuth security, http request must have
//   credentials: 'include' 
//

class AddAssignment extends React.Component {






    render() {
        const columns = [
         { field: 'assignmentName', headerName: 'Assignment Name', width: 250 },
         { field: 'dueDate', headerName: 'Due Date', width: 250},
         { field: 'grade', headerName: 'Grade', width: 150 , editable:true}
         ];


         return(
             <div className="App">
               <Grid container>
                 <Grid item align="left">

                 </Grid>
               </Grid>
               <div style={{ height: 400, width: '100%' }}>

                 <Button id="Submit" variant="outlined" color="primary" style={{margin: 10}} onClick={this.handleSubmit} >
                    Submit
                 </Button>
               </div>
               <ToastContainer autoClose={1500} />   
             </div>
             ); 
         };

}
export default AddAssignment;
